import React, { Component } from 'react';
import Clarifai from 'clarifai';
import Particles from 'react-particles-js';
import Navigation from './components/Navigation/Navigation';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Rank from './components/Rank/Rank';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import SignIg from './components/SignIn/SignIn';
import Register from './components/Register/Register';
import './App.css';


const app = new Clarifai.App({
  apiKey: '0834b90914804703a266eb9233e32201'
 });

const particlesOptions = {
  "particles": {
    "number": { 
        "value": 100
    },
    "size": {
        "value": 3
    }
  } 
};

class App extends Component {

  constructor() {
    super();
    this.state = {
      input: '',
      imageUrl: '',
      box: {},
      route: 'signin',
      isSignedIn: false
    };
  };

  componentDidMount() {
    fetch('http://localhost:3000')
      .then(response => response.json())
      .then(console.log);
  }

  calculateFaceLocation = (data) => {
    const clarifaiFace = data.outputs[0].data.regions[0].region_info.bounding_box;    
    const image = document.getElementById('inputimage');
    const width = (image.clientWidth);
    const heigth = (image.clientHeight);
    /*console.log(clarifaiFace);
    console.log(image);
    console.log('largura',width,'altura',heigth);*/
    return {
      //aqui faz o calculo das "bordas" para pegar os rostos na foto!
      leftCol   : clarifaiFace.left_col * width,
      topRow    : clarifaiFace.top_row * heigth,
      rightCol  : width - (clarifaiFace.right_col * width),
      bottomRow : heigth - (clarifaiFace.bottom_row * heigth)
    };
  };

  displayFaceBox = (box) => {
    console.log(box);
    this.setState({box:box});
    console.log('foto passada para state',this.state.box);
  }

  //pega o valor do textfield e guarda no this.state.input
  onInputChange = (event) => {
    this.setState({ input: event.target.value });
  };

  onButtonSubmit = () => {
    this.setState({imageUrl: this.state.input});
    app.models.predict(Clarifai.FACE_DETECT_MODEL, this.state.input)
    //recupera as regioes detectadas das faces nas fotos
    .then( response => this.displayFaceBox( this.calculateFaceLocation(response) ) )
    .catch( err => console.log(err) )     
  };

  onRouteChange = (route) => {    
    if( route === 'signout' ) {      
      this.setState({isSignedIn: false});
    } else if( route === 'home') {      
      this.setState({isSignedIn: true});
    }
    this.setState({route: route});
  }

  render() {
    const { isSignedIn, imageUrl, route, box} = this.state; //destructing
    return (
      <div className="App">
        <Particles params={particlesOptions} className='particles'/>        
        <Navigation isSignedIn={isSignedIn} onRouteChange={this.onRouteChange} />
        { route === 'home' 
          ? <div>  
              <Logo />
              <Rank />
              <ImageLinkForm 
                onInputChange={this.onInputChange} 
                onButtonSubmit={this.onButtonSubmit}          
              />  
              <FaceRecognition box={box} imageUrl={imageUrl}/>
            </div>
          : ( 
              route === 'signin'
              ? <SignIg onRouteChange={this.onRouteChange}/>             
              : <Register onRouteChange={this.onRouteChange}/>
            )
        }  
      </div>
    );
  }
}

export default App;
